CREATE TABLE `frontdes_auth`.`members` ( `id` INT NOT NULL AUTO_INCREMENT , `email` VARCHAR(50) NOT NULL , `uid` VARCHAR(50) NOT NULL , `password` VARCHAR(50) NOT NULL , `confirmed` TINYINT(1) NOT NULL , `nickname` VARCHAR(50) NOT NULL , `firstName` VARCHAR(50) NOT NULL , `lastName` VARCHAR(50) NOT NULL , `roles` VARCHAR(250) NOT NULL , `session` VARCHAR(100) NOT NULL , `token` TEXT NOT NULL , `status` VARCHAR(10) NOT NULL , `createdAt` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updatedAt` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`), UNIQUE `email` (`email`), UNIQUE `uid` (`uid`), UNIQUE `session` (`session`)) ENGINE = InnoDB;


CREATE TABLE `frontdes_auth`.`memberAttr` ( `id` INT NOT NULL AUTO_INCREMENT , `user_id` INT NOT NULL , `user_uid` VARCHAR(50) NOT NULL , `attr_key` VARCHAR(20) NOT NULL , `attr_value` VARCHAR(200) NOT NULL , `parent` INT NOT NULL , `updatedAt` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , INDEX (`id`)) ENGINE = InnoDB;

CREATE TABLE `frontdes_auth`.`memberDetails` ( `user_id` INT NOT NULL , `uid` VARCHAR(50) NOT NULL , `birthDate` DATE NOT NULL , `language` VARCHAR(10) NOT NULL , `address1` VARCHAR(200) NOT NULL , `address2` VARCHAR(200) NOT NULL , `phone` VARCHAR(50) NOT NULL , `phone2` TEXT NOT NULL , `country` VARCHAR(10) NOT NULL , `email2` TEXT NOT NULL , `metaData` TEXT NOT NULL , `parent` INT NOT NULL , `createdAt` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updatedAt` TIMESTAMP on update CURRENT_TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP , `metaData2` TEXT NOT NULL , INDEX `user_id` (`user_id`), INDEX `uid` (`uid`)) ENGINE = InnoDB;
